  scrollToSection(section: string, sidenav: MatDrawer) {
    // Capture scroll position before closing the nav

    // Scroll to the section
    this.viewportScroller.scrollToAnchor(section);
    this.scrollPosition = this.viewportScroller.getScrollPosition();

    // Close the nav
    sidenav.close();

    // Restore scroll position after the nav closes, with a small delay to allow nav to close
 
      if (this.scrollPosition) {
        this.viewportScroller.scrollToPosition(this.scrollPosition);
        this.scrollPosition = null; // Clear scroll position after restoring
      }
  // Adjust the delay if necessary
  }

 
